import React, { useState, useEffect } from "react";
import "../styles/Works.css";
import "../styles/Experience.css";
import "../styles/Works.css";
import Firstjob from "./Firstjob";
import Aos from "aos";
import "aos/dist/aos.css";

function Works() {
  useEffect(() => {
    Aos.init({ duration: 2000 });
  }, []);
  const [offsetY, setOffsetY] = useState(0);
  const handleScroll = () => setOffsetY(window.pageYOffset);

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div id="works" className="Parallax">
      <h2 data-aos="fade-up" className="heading">
        Works
      </h2>
      <Firstjob  className="FJ1" style={{ transform: `translateY(${offsetY * 0.5}px)` }} />
      <Firstjob   className="FJ1"style={{ transform: "translateY(offsetY * 0.8vw)" }} />
      <Firstjob style={{ transform: `translateY(${offsetY * 1}px)` }} />
    </div>
  );
}

export default Works;
